prerequisite.query:
SELECT COUNT(*) = 1
                     FROM pkghead
                     WHERE pkghead_name = 'u2';

SELECT (SUBSTRING(fetchMetricText('ServerVersion') FOR 1) > '3') 
    OR (SUBSTRING(fetchMetricText('ServerVersion') FOR 1) = '3'
    AND SUBSTRING(fetchMetricText('ServerVersion') FROM 3 FOR 1) >= '6');
